  const input = $json || {};

  // Helper to safely get nested values
  function getValue(obj, path, defaultValue = null) {
    try {
      return path.split('.').reduce((current, key) => current?.[key], obj) ||
  defaultValue;
    } catch {
      return defaultValue;
    }
  }

  // Helper to find values in multiple locations (Vapi format)
  function findValue(key, defaultValue = null) {
    return getValue(input, `parameters.${key}`) ||
           input[key] ||
           getValue(input, `body.${key}`) ||
           getValue(input, `data.${key}`) ||
           getValue(input, `payload.${key}`) ||
           defaultValue;
  }

  // Generate UUID
  function generateUUID() {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {      
      const r = Math.random() * 16 | 0;
      const v = c == 'x' ? r : (r & 0x3 | 0x8);
      return v.toString(16);
    });
  }

  // Extract tool name (Vapi uses 'name' field for function name)
  const tool = input.name || findValue('tool') || findValue('tool_name') ||
  findValue('action') || 'book_appointment';

