  if (!dateStr || !timeStr || !mins) return null;
  const [hh, mm] = String(timeStr).split(':').map(x => parseInt(x, 10));
  if (Number.isNaN(hh) || Number.isNaN(mm)) return null;
  const d = new Date(`${dateStr}T${pad(hh)}:${pad(mm)}:00`);
