);

// Filter appointments for today
const todaysAppointments = existingAppointments
  .map(input => input.json)
  .filter(apt => apt.appointment_date === preferredDate);

console.log('Today appointments:', todaysAppointments.length);

// Check conflicts function
function hasConflict(tech, startTime, endTime) {
  return todaysAppointments.some(apt => {
    if (apt.technician_name !== tech) return false;
    return (startTime >= apt.start_time && startTime < apt.end_time) || 
           (endTime > apt.start_time && endTime <= apt.end_time) ||
           (startTime <= apt.start_time && endTime >= apt.end_time);
  });
}

// Generate available slots
const availableSlots = [];
for (let hour = 9; hour < 18; hour++) {
  const timeSlot = `${hour.toString().padStart(2, '0')}:00`;
  const endTimeSlot = `${(hour + 1).toString().padStart(2, '0')}:00`;

  const availableTechs = qualifiedTechs.filter(tech => 
    !hasConflict(tech, timeSlot, endTimeSlot)
  );

  if (availableTechs.length > 0) {
    const displayHour = hour > 12 ? hour - 12 : hour;
    const ampm = hour >= 12 ? 'PM' : 'AM';

    availableSlots.push({
      start_time: timeSlot,
      display_time: `${displayHour}:00 ${ampm}`,
      technician: availableTechs[0],
      available_techs: availableTechs
    });
  }
}

const bookedSlots = todaysAppointments.slice(0, 5).map(apt => ({
  time: apt.start_time,
  technician: apt.technician_name,
  customer: apt.customer_name
}));

return [{
  json: {
    success: true,
    available_slots: availableSlots.slice(0, 8),
    message: `Found ${availableSlots.length} available slots for ${preferredDate}`,
    service_requested: serviceType,
    qualified_technicians: qualifiedTechs,
    booked_appointments: bookedSlots,
    debug: {
      total_appointments_found: existingAppointments.length,
      appointments_for_date: todaysAppointments.length
    }
  }
}];

// Get input from Execute Workflow trigger
const input = $json;

// Extract search criteria
const customerEmail = input.customer_email;
const customerPhone = input.customer_phone;
const bookingId = input.booking_id;
const dateRange = input.date_range || 'upcoming';
const specificDate = input.specific_date;
const includeCancelled = input.include_cancelled || false;

// Build flexible search criteria
const searchCriteria = {};

// Add customer identification
if (customerEmail) searchCriteria.customer_email = customerEmail;
if (customerPhone) searchCriteria.customer_phone = customerPhone;
if (bookingId) searchCriteria.id = bookingId;

// Add date filtering
const today = new Date().toISOString().split('T')[0];
if (specificDate) {
  searchCriteria.appointment_date = specificDate;
} else {
  switch(dateRange) {
    case 'today':
      searchCriteria.appointment_date = today;
      break;
    case 'upcoming':
      // Will filter in code since Supabase needs >= comparison
      searchCriteria.date_filter = 'upcoming';
      break;
    case 'past':
      // Will filter in code since Supabase needs < comparison  
      searchCriteria.date_filter = 'past';
      break;
    // 'all' means no date filter
  }
}

// Status filtering
if (!includeCancelled) {
  searchCriteria.status_filter = 'active'; // confirmed, rescheduled
}

// Determine search method based on available data
let searchType = 'by_email'; // default

if (bookingId) {
  searchType = 'by_id';
} else if (customerPhone) {
  searchType = 'by_phone';
} else if (customerEmail) {
  searchType = 'by_email';
}

return [{
  json: {
    // Search parameters
    customer_email: customerEmail,
    customer_phone: customerPhone,
    booking_id: bookingId,
    date_range: dateRange,
    specific_date: specificDate,
    include_cancelled: includeCancelled,
    today: today,

    // Search routing
    search_type: searchType,

    // For Supabase query
    search_criteria: searchCriteria,

    // Query settings
    limit: 10, // Don't return too many results
    order_by: 'appointment_date, start_time'
  }
}];

// Process and format appointment results
const allInputs = $input.all();
const searchCriteria = $('Parse Lookup Request').item.json;

console.log('Processing search results:', allInputs.length);

// Collect all appointments from different search methods
let appointments = [];

allInputs.forEach(input => {
  if (input.json && Array.isArray(input.json)) {
    appointments = appointments.concat(input.json);
  } else if (input.json && input.json.id) {
    appointments.push(input.json);
  }
});

console.log('Total appointments found:', appointments.length);

if (appointments.length === 0) {
  return [{
    json: {
      success: false,
      error: 'no_appointments_found',
      message: 'No appointments found with the provided information',
      search_criteria: searchCriteria
    }
  }];
}

// Remove duplicates (in case multiple search methods return same appointment)
const uniqueAppointments = appointments.filter((apt, index, self) => 
  index === self.findIndex(a => a.id === apt.id)
);

// Sort by appointment date (newest first)
uniqueAppointments.sort((a, b) => new Date(b.appointment_date) - new Date(a.appointment_date));

// Separate upcoming and past appointments
const today = new Date().toISOString().split('T')[0];
const upcomingAppointments = uniqueAppointments.filter(apt => 
  apt.appointment_date >= today && apt.status !== 'cancelled'
);
const pastAppointments = uniqueAppointments.filter(apt => 
  apt.appointment_date < today || apt.status === 'cancelled'
);

// Format appointments for response
const formatAppointment = (apt) => ({
  booking_id: apt.id,
  customer_name: apt.customer_name,
  service: apt.service_type,
  date: apt.appointment_date,
  time: apt.start_time,
  duration: `${apt.service_duration} minutes`,
  price: `$${apt.service_price}`,
  technician: apt.technician_name,
  status: apt.status,
  created: apt.created_at?.split('T')[0] || 'N/A'
});

const response = {
  success: true,
  total_appointments: uniqueAppointments.length,
  upcoming_appointments: upcomingAppointments.map(formatAppointment),
  past_appointments: searchCriteria.include_history ? pastAppointments.map(formatAppointment) : [],
  summary: {
    upcoming_count: upcomingAppointments.length,
    past_count: pastAppointments.length,
    cancelled_count: uniqueAppointments.filter(apt => apt.status === 'cancelled').length
  }
};

// If searching by ID and found exactly one, return detailed info
if (searchCriteria.search_type === 'by_id' && uniqueAppointments.length === 1) {
  const apt = uniqueAppointments[0];
  response.appointment_details = {
    ...formatAppointment(apt),
    customer_phone: apt.customer_phone,
    customer_email: apt.customer_email,
    end_time: apt.end_time,
    last_updated: apt.updated_at?.split('T')[0] || 'N/A'
  };
}

return [{ json: response }];

return [{
  json: {
    success: false,
    error: $json.error || "no_appointments_found",
    message: $json.message || "No appointments found with the provided information",
    total_appointments: 0,
    upcoming_appointments: [],
    past_appointments: [],
    summary: {
      upcoming_count: 0,
      past_count: 0,
      cancelled_count: 0
    }
  }
}];

return [{
  json: {
    success: $json.success,
    total_appointments: $json.total_appointments,
    upcoming_appointments: $json.upcoming_appointments,
    past_appointments: $json.past_appointments,
    summary: $json.summary,
    appointment_details: $json.appointment_details || null,
    message: `Found ${$json.total_appointments} appointment(s)`
  }
}];

