console.log('=== DATA FOR SUPABASE/GMAIL ===');
console.log('Full object:', JSON.stringify($json, null, 2));
console.log('Customer email:', $json.customer_email);
console.log('Email valid?', /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test($json.customer_email || ''));
console.log('Booking ID:', $json.booking_id);
console.log('Service duration:', $json.service_duration, typeof $json.service_duration);
console.log('==============================');

return [{ json: $json }];

