// Parse lead data from webhook
const input = $json;

// Extract lead information
const lead = {
  lead_id: `lead_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  business_id: input.business_id || input.businessId,

  // Contact info
  first_name: input.first_name || input.firstName || '',
  last_name: input.last_name || input.lastName || '',
  email: input.email || '',
  phone: input.phone || input.phone_number || '',

  // Lead source tracking
  source: input.source || 'unknown',
  source_campaign: input.campaign || input.utm_campaign || null,
  source_medium: input.medium || input.utm_medium || null,
  source_term: input.term || input.utm_term || null,
  landing_page: input.landing_page || input.page_url || null,
  referrer: input.referrer || null,

  // Lead qualification
  status: 'new',
  lead_score: 0,
  temperature: 'cold',

  // Additional data
  service_interest: input.service_interest || input.service || null,
  message: input.message || input.comments || null,
  metadata: {
    ip_address: input.ip_address,
    user_agent: input.user_agent,
    device_type: input.device_type,
    browser: input.browser
  },

  // Timestamps
  created_at: new Date().toISOString(),
  last_contacted: null,
  next_follow_up: null
};

// Calculate initial lead score
let score = 0;
if (lead.email) score += 20;
if (lead.phone) score += 20;
if (lead.service_interest) score += 15;
if (lead.message && lead.message.length > 50) score += 10;
if (lead.source === 'referral') score += 25;
if (lead.source === 'google_ads') score += 15;

lead.lead_score = score;

// Set temperature based on score
if (score >= 70) lead.temperature = 'hot';
else if (score >= 40) lead.temperature = 'warm';
else lead.temperature = 'cold';

// Determine next action
lead.next_action = score >= 70 ? 'immediate_call' : score >= 40 ? 'same_day_email' : 'nurture_sequence';

console.log(`New lead captured: ${lead.first_name} ${lead.last_name} (Score: ${score}, Temp: ${lead.temperature})`);

return [{ json: lead }];
