// Extract business slug from webhook path
const webhookPath = $node['Business Webhook Router'].json.headers['x-n8n-webhook-url'] || '';
const pathMatch = webhookPath.match(/\/webhook\/([a-zA-Z0-9-]+)/);
const businessSlug = pathMatch ? pathMatch[1] : null;

if (!businessSlug) {
  throw new Error('Invalid webhook path - business slug not found');
}

// Get the business data from Supabase
const supabaseUrl = $env.SUPABASE_URL;
const supabaseKey = $env.SUPABASE_SERVICE_ROLE_KEY;

const axios = require('axios');

try {
  const response = await axios.get(
    `${supabaseUrl}/rest/v1/businesses?slug=eq.${businessSlug}&select=*`,
    {
      headers: {
        'apikey': supabaseKey,
        'Authorization': `Bearer ${supabaseKey}`
      }
    }
  );

  if (response.data.length === 0) {
    throw new Error(`Business not found for slug: ${businessSlug}`);
  }

  const business = response.data[0];

  // Verify webhook token
  const authHeader = $headers.authorization || '';
  const token = authHeader.replace('Bearer ', '');

  if (token !== business.webhook_token) {
    throw new Error('Invalid webhook token');
  }

  return [{
    json: {
      business,
      request: $input.all()[0].json,
      tool: $input.all()[0].json.tool || $input.all()[0].json.name,
      parameters: $input.all()[0].json.parameters || $input.all()[0].json
    }
  }];

} catch (error) {
  throw new Error(`Business lookup failed: ${error.message}`);
}

// Check availability for specific business
const business = $json.business;
const params = $json.parameters;

// Load business-specific schedule and bookings
const supabaseUrl = $env.SUPABASE_URL;
const supabaseKey = $env.SUPABASE_SERVICE_ROLE_KEY;
const axios = require('axios');

try {
  // Check existing appointments for the requested date
  const appointmentsResponse = await axios.get(
    `${supabaseUrl}/rest/v1/appointments?business_id=eq.${business.id}&appointment_date=eq.${params.preferred_date}&status=neq.cancelled`,
    {
      headers: {
        'apikey': supabaseKey,
        'Authorization': `Bearer ${supabaseKey}`
      }
    }
  );

  // Get business hours for the day
  const date = new Date(params.preferred_date);
  const dayOfWeek = date.getDay();

  const hoursResponse = await axios.get(
    `${supabaseUrl}/rest/v1/business_hours?business_id=eq.${business.id}&day_of_week=eq.${dayOfWeek}`,
    {
      headers: {
        'apikey': supabaseKey,
        'Authorization': `Bearer ${supabaseKey}`
      }
    }
  );

  const businessHours = hoursResponse.data[0];
  if (!businessHours || !businessHours.is_open) {
    return [{
      json: {
        result: `We're closed on ${date.toLocaleDateString('en-US', { weekday: 'long' })}s. Please choose another date.`,
        available_slots: []
      }
    }];
  }

  // Calculate available time slots
  const existingAppointments = appointmentsResponse.data;
  const serviceDuration = this.getServiceDuration(params.service_type);
  const availableSlots = this.calculateAvailableSlots(businessHours, existingAppointments, serviceDuration);

  return [{
    json: {
      result: availableSlots.length > 0 
        ? `I found ${availableSlots.length} available slots for ${params.service_type} on ${params.preferred_date}: ${availableSlots.slice(0, 3).join(', ')}${availableSlots.length > 3 ? ' and more' : ''}.`
        : `Sorry, we're fully booked for ${params.service_type} on ${params.preferred_date}. Would you like to try a different date?`,
      available_slots: availableSlots,
      business_name: business.name
    }
  }];

} catch (error) {
  return [{
    json: {
      result: `I'm having trouble checking availability right now. Please call us directly at ${business.phone}.`,
      error: error.message
    }
  }];
}

// Helper function to get service duration
function getServiceDuration(serviceType) {
  const durations = {
    'manicure_signature': 60,
    'manicure_gel': 75,
    'pedicure': 90,
    'combo': 150,
    'enhancement': 120
  };
  return durations[serviceType] || 60;
}

// Helper function to calculate available slots
function calculateAvailableSlots(businessHours, existingAppointments, serviceDuration) {
  const slots = [];
  const openTime = businessHours.open_time;
  const closeTime = businessHours.close_time;

  // Convert times to minutes for easier calculation
  const openMinutes = this.timeToMinutes(openTime);
  const closeMinutes = this.timeToMinutes(closeTime);

  // Generate 30-minute slots
  for (let time = openMinutes; time + serviceDuration <= closeMinutes; time += 30) {
    const timeString = this.minutesToTime(time);

    // Check if this slot conflicts with existing appointments
    const hasConflict = existingAppointments.some(apt => {
      const aptStart = this.timeToMinutes(apt.start_time);
      const aptEnd = aptStart + apt.duration;
      return (time < aptEnd && time + serviceDuration > aptStart);
    });

    if (!hasConflict) {
      slots.push(timeString);
    }
  }

  return slots;
}

function timeToMinutes(timeStr) {
  const [hours, minutes] = timeStr.split(':').map(Number);
  return hours * 60 + minutes;
}

function minutesToTime(minutes) {
  const hours = Math.floor(minutes / 60);
  const mins = minutes % 60;
  return `${hours.toString().padStart(2, '0')}:${mins.toString().padStart(2, '0')}`;
}

// Book appointment for specific business
const business = $json.business;
const params = $json.parameters;

const supabaseUrl = $env.SUPABASE_URL;
const supabaseKey = $env.SUPABASE_SERVICE_ROLE_KEY;
const axios = require('axios');

try {
  // First, find or create customer
  let customer;
  const customerResponse = await axios.get(
    `${supabaseUrl}/rest/v1/customers?email=eq.${params.customer_email}`,
    {
      headers: {
        'apikey': supabaseKey,
        'Authorization': `Bearer ${supabaseKey}`
      }
    }
  );

  if (customerResponse.data.length > 0) {
    customer = customerResponse.data[0];
  } else {
    // Create new customer
    const nameParts = params.customer_name.split(' ');
    const newCustomer = {
      first_name: nameParts[0],
      last_name: nameParts.slice(1).join(' ') || '',
      email: params.customer_email,
      phone: params.customer_phone,
      created_at: new Date().toISOString()
    };

    const createResponse = await axios.post(
      `${supabaseUrl}/rest/v1/customers`,
      newCustomer,
      {
        headers: {
          'apikey': supabaseKey,
          'Authorization': `Bearer ${supabaseKey}`,
          'Content-Type': 'application/json',
          'Prefer': 'return=representation'
        }
      }
    );
    customer = createResponse.data[0];
  }

  // Create business-customer relationship if it doesn't exist
  await axios.post(
    `${supabaseUrl}/rest/v1/business_customers`,
    {
      business_id: business.id,
      customer_id: customer.id,
      first_visit_date: params.appointment_date
    },
    {
      headers: {
        'apikey': supabaseKey,
        'Authorization': `Bearer ${supabaseKey}`,
        'Content-Type': 'application/json'
      }
    }
  ).catch(() => {}); // Ignore if relationship already exists

  // Create the appointment
  const servicePrices = {
    'manicure_signature': 45,
    'manicure_gel': 55,
    'pedicure': 65,
    'combo': 95,
    'enhancement': 85
  };

  const serviceDurations = {
    'manicure_signature': 60,
    'manicure_gel': 75,
    'pedicure': 90,
    'combo': 150,
    'enhancement': 120
  };

  const appointment = {
    business_id: business.id,
    customer_id: customer.id,
    service_name: params.service_type.replace('_', ' '),
    appointment_date: params.appointment_date,
    start_time: params.start_time,
    duration: serviceDurations[params.service_type] || 60,
    total_amount: servicePrices[params.service_type] || 45,
    status: 'confirmed',
    booking_source: 'voice_ai',
    created_at: new Date().toISOString()
  };

  const appointmentResponse = await axios.post(
    `${supabaseUrl}/rest/v1/appointments`,
    appointment,
    {
      headers: {
        'apikey': supabaseKey,
        'Authorization': `Bearer ${supabaseKey}`,
        'Content-Type': 'application/json',
        'Prefer': 'return=representation'
      }
    }
  );

  const createdAppointment = appointmentResponse.data[0];

  // Send confirmation email (simplified)
  const emailContent = {
    to: customer.email,
    subject: `Appointment Confirmed - ${business.name}`,
    body: `Hi ${customer.first_name}!\n\nYour appointment is confirmed:\n\nðŸ“… ${params.appointment_date} at ${params.start_time}\nðŸ’… ${appointment.service_name}\nðŸ¢ ${business.name}\nðŸ“ ${business.address_line1}\nðŸ’° $${appointment.total_amount}\n\nSee you soon!`
  };

  return [{
    json: {
      result: `Perfect! I've booked your ${appointment.service_name} for ${params.appointment_date} at ${params.start_time}. You'll receive a confirmation email at ${customer.email}. Your appointment ID is ${createdAppointment.id.slice(0, 8)}. See you at ${business.name}!`,
      appointment_id: createdAppointment.id,
      confirmation_email: emailContent
    }
  }];

} catch (error) {
  return [{
    json: {
      result: `I'm sorry, I couldn't complete your booking right now. Please call us directly at ${business.phone} and we'll help you right away.`,
      error: error.message
    }
  }];
}

// Check appointments for specific business
const business = $json.business;
const params = $json.parameters;

const supabaseUrl = $env.SUPABASE_URL;
const supabaseKey = $env.SUPABASE_SERVICE_ROLE_KEY;
const axios = require('axios');

try {
  // Find customer by email or phone
  let whereClause = '';
  if (params.customer_email) {
    whereClause = `email=eq.${params.customer_email}`;
  } else if (params.customer_phone) {
    whereClause = `phone=eq.${params.customer_phone}`;
  } else {
    return [{
      json: {
        result: "I'll need your email address or phone number to look up your appointments."
      }
    }];
  }

  const customerResponse = await axios.get(
    `${supabaseUrl}/rest/v1/customers?${whereClause}`,
    {
      headers: {
        'apikey': supabaseKey,
        'Authorization': `Bearer ${supabaseKey}`
      }
    }
  );

  if (customerResponse.data.length === 0) {
    return [{
      json: {
        result: "I don't see any appointments under that information. Could you double-check your email or phone number?"
      }
    }];
  }

  const customer = customerResponse.data[0];

  // Get appointments for this customer at this business
  let dateFilter = '';
  const today = new Date().toISOString().split('T')[0];

  switch (params.date_range || 'upcoming') {
    case 'upcoming':
      dateFilter = `&appointment_date=gte.${today}`;
      break;
    case 'past':
      dateFilter = `&appointment_date=lt.${today}`;
      break;
    case 'today':
      dateFilter = `&appointment_date=eq.${today}`;
      break;
    case 'all':
      dateFilter = '';
      break;
  }

  const appointmentsResponse = await axios.get(
    `${supabaseUrl}/rest/v1/appointments?business_id=eq.${business.id}&customer_id=eq.${customer.id}${dateFilter}&order=appointment_date.asc,start_time.asc`,
    {
      headers: {
        'apikey': supabaseKey,
        'Authorization': `Bearer ${supabaseKey}`
      }
    }
  );

  const appointments = appointmentsResponse.data;

  if (appointments.length === 0) {
    return [{
      json: {
        result: `I don't see any ${params.date_range || 'upcoming'} appointments for you at ${business.name}.`
      }
    }];
  }

  // Format appointment list
  const appointmentList = appointments.map(apt => 
    `${apt.appointment_date} at ${apt.start_time} - ${apt.service_name} (${apt.status})`
  ).join('\n');

  return [{
    json: {
      result: `Here are your ${params.date_range || 'upcoming'} appointments at ${business.name}:\n\n${appointmentList}`,
      appointments: appointments
    }
  }];

} catch (error) {
  return [{
    json: {
      result: `I'm having trouble looking up your appointments. Please call us at ${business.phone}.`,
      error: error.message
    }
  }];
}

// Cancel appointment for specific business
const business = $json.business;
const params = $json.parameters;

const supabaseUrl = $env.SUPABASE_URL;
const supabaseKey = $env.SUPABASE_SERVICE_ROLE_KEY;
const axios = require('axios');

try {
  // Find customer
  const customerResponse = await axios.get(
    `${supabaseUrl}/rest/v1/customers?email=eq.${params.customer_email}`,
    {
      headers: {
        'apikey': supabaseKey,
        'Authorization': `Bearer ${supabaseKey}`
      }
    }
  );

  if (customerResponse.data.length === 0) {
    return [{
      json: {
        result: "I can't find any appointments under that email address. Could you double-check?"
      }
    }];
  }

  const customer = customerResponse.data[0];

  // Find appointments to cancel
  let whereClause = `business_id=eq.${business.id}&customer_id=eq.${customer.id}&status=neq.cancelled`;

  if (params.appointment_date) {
    whereClause += `&appointment_date=eq.${params.appointment_date}`;
  }

  const appointmentsResponse = await axios.get(
    `${supabaseUrl}/rest/v1/appointments?${whereClause}&order=appointment_date.desc`,
    {
      headers: {
        'apikey': supabaseKey,
        'Authorization': `Bearer ${supabaseKey}`
      }
    }
  );

  const appointments = appointmentsResponse.data;

  if (appointments.length === 0) {
    return [{
      json: {
        result: "I don't see any appointments to cancel. Please call us if you need assistance."
      }
    }];
  }

  // Cancel the most recent matching appointment
  const appointmentToCancel = appointments[0];

  await axios.patch(
    `${supabaseUrl}/rest/v1/appointments?id=eq.${appointmentToCancel.id}`,
    {
      status: 'cancelled',
      cancellation_reason: params.cancellation_reason || 'Customer requested',
      cancelled_at: new Date().toISOString()
    },
    {
      headers: {
        'apikey': supabaseKey,
        'Authorization': `Bearer ${supabaseKey}`,
        'Content-Type': 'application/json'
      }
    }
  );

  return [{
    json: {
      result: `I've cancelled your ${appointmentToCancel.service_name} appointment on ${appointmentToCancel.appointment_date} at ${appointmentToCancel.start_time}. You should receive a cancellation confirmation email shortly.`,
      cancelled_appointment: appointmentToCancel
    }
  }];

} catch (error) {
  return [{
    json: {
      result: `I'm having trouble cancelling your appointment. Please call us directly at ${business.phone}.`,
      error: error.message
    }
  }];
}

// Format response for Vapi (simple result object)
const result = $json.result || 'Operation completed';

return [{
  json: {
    result: result
  }
}];

// Handle any errors gracefully
const error = $json.error || 'Unknown error occurred';

return [{
  json: {
    result: "I'm experiencing a technical issue right now. Please call us directly and we'll be happy to help you book your appointment."
  }
}];
